import React from 'react'
import Loading from '../components/Loading'
import { useParams, Link } from 'react-router-dom'
const url = 'https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i='

const SingleCocktail = () => {
  const {id} = useParams();
  const [Loading, setLoading] = React.useState(false);
  const [cocktail, setCocktail] = React.useState(null);

  React.useEffect(async () => {
    setLoading(true);
    await fetch(`${url}${id}`)
      .then(response => response.json())
      .then(data => {
        if(data.drinks){
          const { 
                  strDrink:name, 
                  strDrinkThumb: image, 
                  strAlcoholic: info, 
                  strCategory:category,
                  strGlass: glass,
                  strInstructions: instructions,
                  strIngredient1,
                  strIngredient2,
                  strIngredient3,
                  strIngredient4,
                  strIngredient5,
                 } = data.drinks[0];
          const ingridients = {
                  strIngredient1,
                  strIngredient2,
                  strIngredient3,
                  strIngredient4,
                  strIngredient5,
                }
          const newDrink = {
            name, image, info, category, glass, instructions, ingridients
          }
          setCocktail(newDrink);
        } else {
          setCocktail(null);
          setLoading(false);
        }
      })
  }, [id]);
  if(Loading){
    return(
      <Loading />
    )
  }
  if(!cocktail){
    return(
      <h2 className='section-title'>No cocktail to show</h2>
    )
  }
  const {name, image, category, info, glass, instructions, ingridients} = cocktail;
  return (
    <div>
      <h2>single cocktail page </h2>
    </div>
  )
}

export default SingleCocktail
